create table BANK_TABLE (id bigint generated by default as identity, name varchar(255), primary key (id));
create table CLIENT_TABLE (id bigint generated by default as identity, address varchar(255), email varchar(255), first_name varchar(255), last_name varchar(255), phone_number varchar(255), primary key (id));
create table WORKER_TABLE (id bigint generated by default as identity, first_name varchar(255), last_name varchar(255), phone_number varchar(255), status integer, primary key (id));